    secondary: {
      main: "#11cb5f",
    },
  },
});
 */
const stateRefresh = () => {
  this.setState({
    show_name: "",
    exhibitor: "",
  });
};

function componentDidMount() {
  //this.timer = setInterval(this.progress, 20);
  this.callApi()
    .then((res) => this.setState({ shows: res }))
    .catch((err) => console.log(err));
}

