/*global kakao*/
import React, { useEffect, useRef } from "react";
import Marker from "./Marker";
// import { MapMarker } from "react-kakao-maps-sdk";
import icon from "../../lib/icon/loc7.png";
function makeOverListener(map, marker, infowindow) {
  return function () {
    infowindow.open(map, marker);
  };
}

// 인포윈도우를 닫는 클로저를 만드는 함수입니다
function makeOutListener(infowindow) {
  return function () {
    infowindow.close();
  };
}
function KakaoMap({ lat, lng, showMarker }) {
  const { kakao } = window;
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const mapContainer = useRef(null);

  const galleriesMarker = Marker();

  const imageSize = new kakao.maps.Size(35, 35);
  const galleryMarkerImage = new kakao.maps.MarkerImage(icon, imageSize);

  const mapOptions = {
    center: new kakao.maps.LatLng(lat, lng),
    level: 4,
  };

  useEffect(() => {
    // const container = document.getElementById("map");
    const map = new kakao.maps.Map(mapContainer.current, mapOptions);

    if (showMarker) {
      const markerPosition = new kakao.maps.LatLng(lat, lng);
      const marker = new kakao.maps.Marker({
        map: map,
        position: markerPosition,
      });
    }

    galleriesMarker.map((galleryMarker) => {
      const setGalleryMarker = new kakao.maps.Marker({
        map: map,
        position: new kakao.maps.LatLng(
          galleryMarker.latlng.lat,
          galleryMarker.latlng.lng
        ),
        image: galleryMarkerImage,
      });
      const infowindow = new kakao.maps.InfoWindow({
        content: galleryMarker.title,
      });
      const overlay = new kakao.maps.CustomOverlay({
        content: content,
        map: map,
        position: setGalleryMarker.getPosition(),
      });
      kakao.maps.event.addListener(setGalleryMarker, "click", function () {
        overlay.setMap(map);
      });
    });
  }, [lat, lng, showMarker]);

  return (
    <div>
      <div
        id="map"
        ref={mapContainer}
        style={{ width: "100%", height: `${mapHeight}px` }}
      ></div>
    </div>
  );
}

export default React.memo(KakaoMap);
