import axios from "axios";

export const fetchShowsbySearchQuery = async (searchQuery) => {
  try {
    const url = searchQuery ? `/api/shows/search/${searchQuery}` : "/api/shows";
    const response = await axios.get(`http://localhost:5100${url}`);
    // const response = await axios.get(`https://exhi-lover.com${url}`);
    return response.data;
  } catch (error) {
    throw new Error("Failed to fetch shows");
  }
};
