/*global kakao*/
import React, { useEffect, useState } from "react";
import { Map, MapMarker, StaticMap } from "react-kakao-maps-sdk";

function MiniMap({ name, lat, lng }) {
  useEffect(() => {
    const marker = {
      position: new window.kakao.maps.LatLng(lat, lng),
      text: name,
    };

    const staticMapContainer = document.getElementById("staticMap");
    const staticMapOption = {
      center: new window.kakao.maps.LatLng(lat, lng),
      level: 3,
      marker: marker,
    };

    const staticMap = new window.kakao.maps.StaticMap(
      staticMapContainer,
      staticMapOption
    );

    return () => {
      // Clean up the map instance when the component unmounts
      staticMap.removeAllChildMarkers();
    };
  }, []); // Empty dependency array ensures that this effect runs only once

  return <div id="staticMap" style={{ width: "100%", height: "340px" }}></div>;
