import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Post from "./Post";
import Typography from "@mui/material/Typography";
import axio from "axios";
import axios from "axios";

/*
const theme = createTheme({
  palette: {
    primary: {
      main: purple[400],
    },
    secondary: {
      main: "#11cb5f",
    },
  },
});
 */
const show_info = [
  {
    show_name: "감각의 공간, 워치 앤 칠 2.0",
    show_artist:
      "김실비, 김아영, 염지혜, 김웅현, 안정주&전소정, 바래, 안드레아스 바너슈테트, 제나 수텔라, 왕 & 쇠데르스트룀, 바스마 알 샤리프, 샤리프 와키드, 유리 패티슨, 시몬 C. 니키유, ASMR티카, 알리 체리, 마하 마아문, 아마드 고세인",
    show_search:
      "감각의 공간, 워치 앤 칠 2.0김실비, 김아영, 염지혜, 김웅현, 안정주&전소정, 바래, 안드레아스 바너슈테트, 제나 수텔라, 왕 & 쇠데르스트룀, 바스마 알 샤리프, 샤리프 와키드, 유리 패티슨, 시몬 C. 니키유, ASMR티카, 알리 체리, 마하 마아문, 아마드 고세인",
    show_term: "2022-06-10 ~ 2022-09-12",
    show_num: "20여점",
    show_place: "국립현대미술관(서울)",
    show_place_eng: "mmca",
    show_place_detail: "서울 지하1층, 6전시실, 온라인 플랫폼",
    show_price: "서울관통합권 4,000원",
    show_link: "https://www.kguide.kr/mmca001/",
    show_hashtag: "",
    show_imgs: 0,
  },
  {
    show_name: "나너의 기억",
    show_artist:
      "루이즈 부르주아, 뮌, 박혜수, 세실리아 비쿠냐, 송주원, 시프리앙 가이야르, 아크람 자타리, 안리 살라, 앤디 워홀, 양정욱, 임윤경, 허만 콜겐, 홍순명 총 13명(팀)",
    show_search:
      "나너의 기억루이즈 부르주아, 뮌, 박혜수, 세실리아 비쿠냐, 송주원, 시프리앙 가이야르, 아크람 자타리, 안리 살라, 앤디 워홀, 양정욱, 임윤경, 허만 콜겐, 홍순명 총 13명(팀)",
    show_term: "2022-04-08 ~ 2022-08-07",
    show_num: "13점",
    show_place: "국립현대미술관(서울)",
    show_place_eng: "mmca",
    show_place_detail: "서울 지하1층, 5전시실",
    show_price: "서울관통합권 4,000원",
    show_link: "https://www.kguide.kr/mmca001/",
    show_hashtag: "",
    show_imgs: 0,
  },
  {
    show_name: "히토 슈타이얼 - 데이터의 바다",
    show_artist: "히토 슈타이얼",
    show_search: "히토 슈타이얼 - 데이터의 바다히토 슈타이얼",
    show_term: "2022-04-29 ~ 2022-09-18",
    show_num: "미디어, 영상 설치, 오브제 등 23",
    show_place: "국립현대미술관(서울)",
    show_place_eng: "mmca",
    show_place_detail: "서울 지하1층, 2, 3, 4전시실, 프로젝트 갤러리",
    show_price: "서울관통합권 4,000원",
    show_link: "https://www.kguide.kr/mmca001/",
    show_hashtag: "",
    show_imgs: 0,
  },
];

function Search() {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchShows = async () => {
      try {
        // 요청이 시작 할 때에는 error 와 users 를 초기화하고
        setError(null);
        setShows(null);
        // loading 상태를 true 로 바꿉니다.
        const response = await axios.get("/api/shows");
        setShows(response.data); // 데이터는 response.data 안에 들어있습니다.
      } catch (e) {
        setError(e);
      }
    };

    fetchShows();
  }, []);

  if (!shows) return null;

  return (
    <>
      <Paper
        sx={{
          backgroundColor: "#2a2a2a",
          //width: "30ch",
          width: "90%",
          maxWidth: "30ch",
          margin: "auto",
          paddingBottom: "20px",
          paddingTop: "10px",
        }}
      >
        <Box
          component="form"
          sx={{
            "& .MuiTextField-root": { m: 1, width: "25ch" },
            margin: "auto",
          }}
          noValidate
          autoComplete="off"
        >
          <Typography variant="subtitle2" sx={{ color: "#EC6C03" }}>
            전시회, 작가 이름 검색
          </Typography>

          <TextField
            id="standard-search"
            type="search"
            variant="standard"
            color="warning"
            inputProps={{ style: { color: "#ECD3BF" } }}
            focused
          />
        </Box>
      </Paper>
      <Paper
        sx={{
          backgroundColor: "#1b1b1b", //#1b1b1b
          margin: "auto",
          width: "100%",
          paddingBottom: "10px",
          paddingTop: "60px",
        }}
      >
        {show_info.map((c) => {
          return (
            <Post
              show_name={c.show_name}
              show_artist={c.show_artist}
              show_place={c.show_place}
            />
          );
        })}
      </Paper>
    </>
  );
}

export default Search;
