import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardCover from "@mui/joy/CardCover";
import Typography from "@mui/joy/Typography";
import Link from "@mui/joy/Link";
import { TableRow, TableCell, createTheme, ThemeProvider } from "@mui/material";
import Button from "@mui/joy/Button";
import ButtonGroup from "@mui/material/ButtonGroup";

function Post({ show_name, show_place }) {
  return (
    <Card
      sx={{
        //minWidth: 150,
        //width: "30ch"
        width: "90%",
        maxWidth: "30ch",
        height: "auto",
        margin: "auto",
        bgcolor: "#131313", //initial
        boxShadow: "none",
        "--Card-padding": "10px",
        // post.js 영역 넓이
      }}
    >
      <Box
        sx={{
          position: "relative", //이미지
          margin: "auto",
          marginTop: "20px",
          width: "90%",
          maxWidth: "30ch",
          backgroundColor: "primary.dark",
          "&:hover": {
            backgroundColor: "primary.main",
            opacity: [0.9, 0.8, 0.7],
          },
        }}
      >
        <Box
          component="img"
          sx={{
            height: "100%",
            width: "90%",
            margin: "auto",
            maxWidth: "30ch", //박스를 쓰지말고 img 태그를 사용하면서 alt, width, height 설정해주고
          }}
          src="/upload/mmca/seoul/감각의 공간, 워치 앤 칠 2.0/1.png"
        />
        <CardCover
          className="gradient-cover"
          sx={{
            "&:hover, &:focus-within": {
              opacity: 1,
            },
            //opacity: 0, 이걸 넣으면 마우스를 올리거나 클리하면 생기고 때면 사라짐. 즉 나한테 필요 없뜸
            transition: "0.1s ease-in",
            paddingRight: "10px",
          }}
        >
          {/* The first box acts as a container that inherits style from the CardCover */}
          <Box>
            {/*shows_name 크기 */}
            <Box
              sx={{
                p: 2,
                display: "flex",
                alignItems: "center",
                gap: 1.5,
                flexGrow: 1,
                alignSelf: "flex-end",
                width: "20ch",
              }}
            >
              <Typography
                level="h2"
                noWrap
                sx={{ fontSize: "lg" }}
              ></Typography>
            </Box>
          </Box>
        </CardCover>
      </Box>
      <Box
        sx={{
          display: "flex",
          gap: 1,
          mt: 1.5,
          alignItems: "center",
          paddingBottom: "10px",
        }}
      >
        <Avatar
          src="https://images.unsplash.com/profile-1502669002421-a8d274ad2897?dpr=2&auto=format&fit=crop&w=32&h=32&q=60&crop=faces&bg=fff"
          size="sm"
          sx={{ "--Avatar-size": "1.5rem" }}
        />
        //
        <TableRow>
          <TableCell sx={{ borderBottom: "none" }}>
            <Link
              href="#dribbble-shot"
              overlay
              underline="none"
              sx={{
                color: "#aaaaaa",
                textOverflow: "ellipsis",
                overflow: "hidden",
                display: "block",
                width: "8ch",
              }}
            >
              {show_place}
            </Link>
          </TableCell>
          <TableCell
            sx={{
              borderBottom: "none",
              textOverflow: "ellipsis",
            }}
          >
            <Link
              href="#dribbble-shot"
              overlay
              underline="none"
              sx={{
                color: "#dddddd",
                textOverflow: "ellipsis",
                overflow: "hidden",
                display: "block",
                width: "13ch",
                textAlign: "",
              }}
            >
              {show_name}
            </Link>
          </TableCell>
        </TableRow>
      </Box>
      <Box>
        <Typography
          textAlign={"left"}
          paddingLeft={"10px"}
          sx={{ color: "#cccccc" }}
        >
          #a #a #a
        </Typography>
      </Box>
    </Card>
  );
}

export default Post;
