/* eslint-disable jsx-a11y/anchor-is-valid */
import * as React from "react";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardOverflow from "@mui/joy/CardOverflow";
import Typography from "@mui/joy/Typography";
import { createTheme, styled } from "@mui/material/styles";
import ButtonBase from "@mui/material/ButtonBase";

const ImageButton = styled(ButtonBase)(({ theme }) => ({
  position: "relative",
  height: 350,
  [theme.breakpoints.down("sm")]: {
    //"100% !important", // Overrides inline-style
    height: 350,
  },
  "&:hover, &.Mui-focusVisible": {
    zIndex: 1,
    "& .MuiImageBackdrop-root": {
      opacity: 0.15,
    },
    "& .MuiImageMarked-root": {
      opacity: 0,
    },
    "& .MuiTypography-root": {
      border: "4px solid currentColor",
    },
  },
}));

export default function Detail({
  show_name,
  show_artist,
  show_search,
  show_term,
  show_num,
  show_city,
  show_place,
  show_place_eng,
  show_place_detail,
  show_price,
  show_hashtag,
  show_imgs,
}) {
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/1.png";

  const images = [];

  const createImageArray = () => {
    for (let i = 2; i < show_imgs + 1; i++) {
      images.push({
        url:
          "/upload/seoul/" +
          show_place_eng +
          "/" +
          show_name +
          "/" +
          i +
          ".png",
        title: i,
        height: "350px",
        width: "100%",
      });
    }
    return images;
  };

  createImageArray();
  return (
    <Card
      sx={{
        "--Card-radius": (theme) => theme.vars.radius.xs,
        bgcolor: "#eeeeee",
        width: "91%",
        margin: "auto",
        padding: "auto",
      }}
    >
      <CardOverflow>
        <Box
          component="img"
          sx={{
            paddingTop: "10px",
            width: "100%",
            margin: "auto", //박스를 쓰지말고 img 태그를 사용하면서 alt, width, height 설정해주고
          }}
          src={imgSrc}
        />
      </CardOverflow>
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          mx: -1,
          my: 1,
        }}
      >
        <Box
          sx={{
            width: "100%",
            height: "350px",
            margin: "auto",
          }}
        >
          <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              overflow: "hidden",
              overflowX: "scroll",
              height: "350px",
              marginTop: "15px",
              marginBottom: "15px",
            }}
          >
            {images.map((i) => (
              <ImageButton
                focusRipple
                key={i.title}
                style={{
                  height: 350,
                }}
              >
                <Box
                  component="img"
                  sx={{
                    height: "100%",
                    margin: "auto",
                  }}
                  src={i.url}
                />
              </ImageButton>
            ))}
          </Box>
        </Box>
      </Box>
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          pb: 1.5,
          gap: 1,
          paddingTop: "15px",
        }}
      >
        {/*<Box
          sx={{
            position: "relative",
            "&:before": {
              content: '""',
              position: "absolute",
              top: 0,
              left: 0,
              bottom: 0,
              right: 0,
              m: "-2px",
              borderRadius: "50%",
              background:
                "linear-gradient(45deg, #f09433 0%,#e6683c 25%,#dc2743 50%,#cc2366 75%,#bc1888 100%)",
            },
          }}
        >
          <Avatar size="sm" src="/static/logo.png" sx={{ p: 0.5, border: "1px solid", borderColor: "background.body" }} /> 
        </Box>*/}
        <Typography fontWeight="bold" fontSize="xl">
          {show_place}
        </Typography>
      </Box>
      <Typography fontWeight="bold" fontSize="lg" gutterBottom>
        {show_name}
      </Typography>
      <Typography
        fontWeight="500"
        fontSize="md"
        gutterBottom
        sx={{ paddingTop: "3px" }}
      >
        - 참여 작가
      </Typography>
      <Typography fontSize="sm" gutterBottom>
        {show_artist}
      </Typography>
      <Typography
        fontWeight="500"
        fontSize="md"
        gutterBottom
        sx={{ paddingTop: "3px" }}
      >
        - 기간
      </Typography>
      <Typography fontSize="sm" gutterBottom>
        {show_term}
      </Typography>
      <Typography
        fontWeight="500"
        fontSize="md"
        gutterBottom
        sx={{ paddingTop: "3px" }}
      >
        - 작품 수
      </Typography>
      <Typography fontSize="sm" gutterBottom>
        {show_num}
      </Typography>
      <Typography
        fontWeight="500"
        fontSize="md"
        gutterBottom
        sx={{ paddingTop: "3px" }}
      >
        - 상세 장소
      </Typography>
      <Typography fontSize="sm" gutterBottom>
        {show_place_detail}
      </Typography>
      <Typography
        fontWeight="500"
        fontSize="md"
        gutterBottom
        sx={{ paddingTop: "3px" }}
      >
        - 가격
      </Typography>
      <Typography fontSize="sm" gutterBottom>
        {show_price}
      </Typography>

      <Typography
        fontWeight="500"
        fontSize="md"
        gutterBottom
        sx={{ paddingTop: "3px" }}
      >
        - 해쉬 태그
      </Typography>
      <Typography fontSize="sm" gutterBottom>
        {show_hashtag}
      </Typography>
    </Card>
  );
}
