import * as React from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import ButtonBase from "@mui/material/ButtonBase";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";

const images = [
  {
    url: "/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/1.png",
    title: "1",
    width: "50%",
  },
  {
    url: "/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/2.png",
    title: "2",
    width: "30%",
  },
  {
    url: "/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/3.png",
    title: "3",
    width: "45%",
  },
];

const ImageButton = styled(ButtonBase)(({ theme }) => ({
  position: "relative",
  height: 350,
  [theme.breakpoints.down("sm")]: {
    width: "30px", //"100% !important", // Overrides inline-style
    height: 350,
  },
  "&:hover, &.Mui-focusVisible": {
    zIndex: 1,
    "& .MuiImageBackdrop-root": {
      opacity: 0.15,
    },
    "& .MuiImageMarked-root": {
      opacity: 0,
    },
    "& .MuiTypography-root": {
      border: "4px solid currentColor",
    },
  },
}));

const ImageSrc = styled("span")({
  position: "absolute",
  left: 0,
  right: 0,
  top: 0,
  bottom: 0,
  backgroundSize: "cover",
  backgroundPosition: "center 40%",
});

const ImageBackdrop = styled("span")(({ theme }) => ({
  position: "absolute",
  left: 0,
  right: 0,
  top: 0,
  bottom: 0,
  backgroundColor: theme.palette.common.black,
  opacity: 0.4,
  transition: theme.transitions.create("opacity"),
}));

export default function ImageList() {
  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "row",
        overflow: "hidden",
        overflowX: "scroll",
        height: "350px",
        paddingTop: "15px",
        paddingBottom: "15px",
      }}
    >
      {images.map((i) => (
        <ImageButton
          focusRipple
          key={i.title}
          style={{
            width: i.width,
          }}
        >
          <ImageSrc style={{ backgroundImage: `url(${i.url})` }} />
          <ImageBackdrop className="MuiImageBackdrop-root" />
        </ImageButton>
      ))}
    </Box>
  );
}
