const PostBlock = styled.div`
  padding: 5px 5px 30px 5px;
  width: 95%;
  margin: 0 auto;
`;

const ContextBoxes = styled.div`
  margin: auto;
  width: 100%;
  max-width: 45rem;
`;

const HoverEffect = styled.div`
  background: #131313;

  &:hover {
    // & > div {
    //   background: #ec6c03;
    //   h1,
    //   h2,
    //   h3 {
    //     color: black;
    //     text-decoration: underline;
    //   }
    //   h4,
    //   h5 {
    //     color: black;
    //   }
    //   strong {
    //     color: black;
    //   }
    // }

    img {
      transform: scale(1.1);
    }
  }
`;

const ContextBox = styled.div`
  padding: 15px 15px 15px 24px;
  flex: 1 0 auto;
  &:hover {
    background: #ec6c03;
    h1,
    h2,
    h3 {
      color: black;
      text-decoration: underline;
    }
    h4,
    h5 {
      color: black;
    }
    strong {
      color: black;
    }
  }
`;
const PostTitle = styled.div`
  text-align: left;
  color: white;
  strong {
    color: #ec6c03;
  }
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const PostContent = styled.div`
  text-align: left;
  font-family: "Happiness-Sans-Title", sans-serif;
  h1,
  h2,
  h3,
  h4 {
    color: white;
  }
  h5 {
    color: lightgray;
  }
`;

const ImageBlock = styled.div`
  width: 100%;
  height: 0;
  padding-bottom: 56.25%; /* maintain 16:9 aspect ratio */
  position: relative;
  overflow: hidden;
  img {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.6s ease;
  }
`;

const Post = ({ show, key }) => {
  console.log(show);
  const {
    show_name,
    show_artist,
    show_search,
    show_term,
    show_num,
    show_city,
    show_place,
    show_place_eng,
    show_place_detail,
    show_price,
    show_link,
    show_hashtag,
    show_imgs,
  } = show;
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/3.png";
  const [open, setOpen] = useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };
  return (
    <>
      <PostBlock>
        <ContextBoxes>
          <HoverEffect>
            <ContextBox>
              <PostTitle>
                <h2>
                  {show_place}
                  <br />
                  <strong>{show_name}</strong>
                </h2>
              </PostTitle>
              <PostContent>
                <h4>{show_term}</h4>
              </PostContent>
            </ContextBox>

            <ImageBlock>
              <img src={imgSrc} alt="" loading="lazy" />
            </ImageBlock>
          </HoverEffect>
        </ContextBoxes>
      </PostBlock>
    </>
  );
};

export default Post;
