import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import { createTheme, ThemeProvider } from "@mui/material/styles";

const theme = createTheme({
  palette: {
    white: {
      main: "#ffffff",
      contrastText: "#ffffff",
    },
  },
});
const SearchBar = () => {
  return (
    <>
      <Paper
        sx={{
          backgroundColor: "#2a2a2a",
          //width: "30ch",
          width: "90%",
          maxWidth: "30rem",
          margin: "auto",
          paddingBottom: "20px",
          paddingTop: "20px",
        }}
      >
        <Box
          component="form"
          sx={{
            "& .MuiTextField-root": { m: 1, width: "25ch" },
            margin: "auto",
          }}
          noValidate
          autoComplete="off"
        >
          <Typography variant="subtitle2" sx={{ color: "#EC6C03" }}>
            전시회, 작가 이름 검색
          </Typography>
          <TextField
            id="standard-search"
            type="search"
            variant="standard"
            color="warning"
            inputProps={{ style: { color: "#ECD3BF" } }}
            focused
          />
        </Box>
      </Paper>
    </>
  );
};

export default SearchBar;
