import styled from "styled-components";
import InstagramIcon from "@mui/icons-material/Instagram";
import SentimentSatisfiedAltIcon from "@mui/icons-material/SentimentSatisfiedAlt";
import { Link } from "react-router-dom";

const FootBox = styled.div`
  bottom: 0;
  width: 100%;
  background: black;
`;

const Foot = styled.div`
  color: white;
  padding-left: 0.8rem;
  padding-bottom: 0.4rem;
  font-family: "Open Sans", sans-serif;
  font-weight: bold;
  p {
    margin: 0;
    Link {
      display: flex;
      alignitems: center;
    }
    svg {
      transform: translateY(3px);
      font-size: 1.2rem;
      margin-right: 0.2rem;
    }
    span {
      color: #ffffff;
    }
  }
`;

const HrLine = styled.hr`
  width: 96%;
  border: none;
  border-top: 1px solid #868686; /* change the color to blue and thickness to 2px */
`;

const Footer = () => {
  return (
    <FootBox>
      <HrLine />
      <Foot>
        <p>
          2023 Exhi-lover published <br />
          MADE BY <InstagramIcon />
          <Link to="https://www.instagram.com/exhi_lover/" target="_blank">
            <span> EXHI_LOVER</span>
          </Link>
        </p>
        <p>Art is Life. Life is Art.</p>
        {/* "Progress over Perfection" 
        "Continuous Improvement" "Better Every Day"
        "Self-Improvement is the Key to Success" 
        "Invest in Yourself" "Become
        the Best Version of Yourself" 
        "Empower Your Inner Self" 
        "Personal Growth is a Lifelong Journey" 
        "Choose to Thrive, not just Survive" 
        "Challenge Yourself to Grow" 
        "Personal Growth is a Lifelong Journey"*/}
      </Foot>
    </FootBox>
  );
};

export default Footer;
