import axios from "axios";
const mainUrl = "http://localhost:5100";
// const mainUrl = "https://exhi-lover.com";

// search.js 검색 관련 api
export const fetchShowsbySearchQuery = async (searchQuery) => {
  const url = searchQuery ? `/api/shows/search/${searchQuery}` : "/api/shows";
  const response = await axios.get(`${mainUrl}${url}`);
  return response.data;
};

// postTemplate.js 각 전시회의 해당 tag id 불러오기
export const fetchShowTagsId = async (showId) => {
  const url = `/api/exhibition_tags/shows/${showId}`;
  const response = await axios.get(`${mainUrl}${url}`);
  return response.data;
};

export const fetchTagName = async (tagId) => {
  const url = `/api/tags/${tagId}`;
  const response = await axios.get(`${mainUrl}${url}`);
  return response.data[0].tag_name;
};
