import styled from "styled-components";
import GlobalStyle from "../../lib/styles/fontStyle";
import { Link } from "react-router-dom";
import FileDownloadDoneSharpIcon from "@mui/icons-material/FileDownloadDoneSharp";
import HorizontalLine from "../common/HorizontalLine";

const DetailViewerBlock = styled.div`
  width: 100%;
  div {
    text-align: left;
  }
  Booking {
    display: flex;
    justify-content: center;
    align-items: center;
    color: black;
  }
`;

const MainImage = styled.img`
  padding-left: 0.8rem;
  width: 100%;
  aspect-ratio: 16/9; /* change to the desired aspect ratio */
  object-fit: cover;
  overflow: hidden;
  display: flex;
  align-items: center;
  border-bottom: 0.03rem solid #202020;
`;

const MainTitle = styled.div`
  padding-left: 0.8rem;
  color: white;
  padding-top: 1.2rem;
  p {
    font-size: 1.5rem;
    font-family: = "Happiness-Sans-Bold", sans-serif;
    margin: 0.4rem 0;
  }
  strong {
    font-size: 1.8rem;
    color: ${(props) => props.color || "#ec6c03"};
    font-family: = "Happiness-Sans-Title", sans-serif;
  }
`;

const Term = styled.div`
  padding-left: 0.8rem;
  color: white;
  padding-top: 1.1rem;
  font-size: 0.83rem;
`;

const BookingArea = styled.div`
  margin-top: 0.45rem;
`;
const Booking = styled.div`
  background: ${(props) => props.color || "#ec6c03"};
  width: 93%;
  height: 2.8rem;
  color: #000000;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: auto;
  padding-right: 1.7rem;
  p {
    padding-left: 1rem;
    font-size: 1.1rem;
    font-family: "Happiness-Sans-Bold";
  }
  svg {
    // margin-left: 1rem; /* add margin to the left side */
    font-size: 1.6rem;
  }
  &:hover {
    color: white;
    background: #000000;
    border: 1px solid #ffffff;
  }
`;

const InfoBlock = styled.div`
  width: 100%;
`;

const Artist = styled.div`
  padding-top: 1rem;
  padding-left: 0.8rem;
  padding-right: 0.8rem;
  color: white;
  font-family: "Happiness-Sans-Bold";
  font-size: 1rem;
  line-height: 14;
  p {
    font-size: 1.4rem;
    margin: 0 0 0.1rem 0;
    font-family: "Happiness-Sans-Title";
  }
`;

const Tags = styled.div``;

const DetailViewer = ({ show, color }) => {
  console.log({ show });
  const {
    show_name,
    show_artist,
    show_search,
    show_term,
    show_num,
    show_city,
    show_place,
    show_place_eng,
    show_place_detail,
    show_price,
    show_link,
    show_hashtag,
    show_imgs,
  } = show[0];
  const booking_site = null;
  const imgSrc = "/upload/seoul/" + show_place_eng + "/" + show_name + "/1.png";
  return (
    <>
      <GlobalStyle />
      <DetailViewerBlock>
        <MainImage src={imgSrc} alt="" loading="lazy" />
        <MainTitle color={color}>
          <p>{show_place}</p>
          <strong>{show_name}</strong>
        </MainTitle>
        <Term>{show_term}</Term>
        <Link to={show_link}>
          <BookingArea>
            <Booking color={color}>
              <p>예약하기</p>
              <FileDownloadDoneSharpIcon />
            </Booking>
          </BookingArea>
        </Link>
        <InfoBlock>
          <Artist>
            <p>참여 작가</p>
            {show_artist}
          </Artist>
          <HorizontalLine /> aserewrawe
        </InfoBlock>
        <Tags>
          <div className="tag">#tag1</div>
          <div className="tag">#tag2</div>
          <div className="tag">#tag3</div>
        </Tags>
      </DetailViewerBlock>
    </>
  );
};

export default DetailViewer;
