import * as React from "react";
import { useState, useEffect } from "react";
import styled from "styled-components";

import { era, genre, takePhoto, artMovement, orient, database } from "./Tags";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";

const Box = styled.div`
  algin-item: left;
  width: 100%;
  padding: 0.4rem 0 0 1.1rem;
  margin: 0 auto;
  padding: 5px 5px 30px 5px;
`;

function HashTag() {
  const [checkedItems, setCheckedItems] = useState([]);

  useEffect(() => {
    console.log("Checked items: ", checkedItems);
  }, [checkedItems]);

  const handleCheck = (newCheckedItems) => {
    setCheckedItems(newCheckedItems, () => {
      console.log("Updated checked items: ", checkedItems);
    });
  };

  return (
    <>
      <Box>
        <HorizontalLine />
        <TagsCheckBox
          title={"촬영 가능 여부"}
          items={takePhoto}
          handleCheck={handleCheck}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"지역별"}
          items={orient}
          handleCheck={handleCheck}
        />
        <HorizontalLine />
        <TagsCheckBox
          title={"장르별"}
          items={genre}
          handleCheck={handleCheck}
        />
        <HorizontalLine />
        <TagsCheckBox title={"시대별"} items={era} handleCheck={handleCheck} />
        <HorizontalLine />
        <TagsCheckBox
          title={"미술사조"}
          items={artMovement}
          handleCheck={handleCheck}
        />
        <HorizontalLine />
      </Box>
    </>
  );
}

export default HashTag;
