import React from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import Paper from "@mui/material/Paper";
import Post from "./Post";
import Typography from "@mui/material/Typography";

/*
const theme = createTheme({
  palette: {
    primary: {
      main: purple[400],
    },
    secondary: {
      main: "#11cb5f",
    },
  },
});
 */

function Search() {
  return (
    <>
      <Paper
        sx={{
          backgroundColor: "#2a2a2a",
          //width: "30ch",
          width: "90%",
          maxWidth: "30ch",
          margin: "auto",
          paddingBottom: "20px",
          paddingTop: "10px",
        }}
      >
        <Box
          component="form"
          sx={{
            "& .MuiTextField-root": { m: 1, width: "25ch" },
            margin: "auto",
          }}
          noValidate
          autoComplete="off"
        >
          <Typography variant="subtitle2" sx={{ color: "#EC6C03" }}>
            전시회, 작가 이름 검색
          </Typography>

          <TextField
            id="standard-search"
            type="search"
            variant="standard"
            color="warning"
            inputProps={{ style: { color: "#ECD3BF" } }}
            focused
          />
        </Box>
      </Paper>
      <Paper
        sx={{
          backgroundColor: "#1b1b1b", //#1b1b1b
          margin: "auto",
          width: "100%",
          paddingBottom: "10px",
          paddingTop: "60px",
        }}
      >
        <Post />
      </Paper>
    </>
  );
}

export default Search;
