/*global kakao*/
import React, { useEffect } from "react";
import { Map, MapMarker } from "react-kakao-maps-sdk";

function MiniMap({ lat, lng }) {
  return (
    <Map
      center={{ lat: lat, lng: lng }}
      style={{ width: "100%", height: "21rem" }}
      level={4}
      draggable={false}
    >
      <MapMarker position={{ lat: lat, lng: lng }}></MapMarker>
    </Map>
  );
}
