/*global kakao*/
import React, { useEffect, useRef, useState } from "react";
import icon from "../../lib/icon/loc.png";
import iconDark from "../../lib/icon/dot.png";
import SwipeableEdgeDrawer from "./SwipeableEdgeDrawer";

function KakaoMap({ lat, lng, galleriesMarker }) {
  const mapHeight = (window.innerWidth >= 560 ? 560 : window.innerWidth) * 1.1;
  const mapContainer = useRef(null);

  const [selectedMarker, setSelectedMarker] = useState(null);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [lastClickedMarker, setLastClickedMarker] = useState(null);

  const imageSize = new window.kakao.maps.Size(35, 35);
  const darkImageSize = new window.kakao.maps.Size(16, 16);
  const galleryMarkerImage = new window.kakao.maps.MarkerImage(icon, imageSize);
  const galleryMarkerImageDark = new window.kakao.maps.MarkerImage(
    iconDark,
    darkImageSize
  );

  const mapOptions = {
    center: new window.kakao.maps.LatLng(lat, lng),
    level: 6,
  };

  const handleMarkerClick = (marker, galleryMarker) => {
    marker.panTo(marker.getPosition());
    window.scrollTo({ top: document.body.scrollHeight, behavior: "smooth" });
    setSelectedMarker(galleryMarker);
    setLastClickedMarker(marker.getPosition());
    localStorage.setItem(
      "lastClickedMarker",
      JSON.stringify({
        lat: marker.getPosition().getLat(),
        lng: marker.getPosition().getLng(),
      })
    );
    setDrawerOpen(true);
  };

  const createMarker = (galleryMarker, map) => {
    const isOnDisplay = galleryMarker.onDisplay;
    const markerImage = isOnDisplay
      ? galleryMarkerImage
      : galleryMarkerImageDark;

    const marker = new window.kakao.maps.Marker({
      map: map,
      position: new window.kakao.maps.LatLng(
        galleryMarker.latlng.lat,
        galleryMarker.latlng.lng
      ),
      image: markerImage,
      zIndex: isOnDisplay ? 99 : 1,
    });

    window.kakao.maps.event.addListener(marker, "click", () =>
      handleMarkerClick(marker, galleryMarker)
    );
  };

  useEffect(() => {
    const map = new window.kakao.maps.Map(mapContainer.current, mapOptions);

    const storedLastClickedMarker = localStorage.getItem("lastClickedMarker");
    const initialLastClickedMarker = storedLastClickedMarker
      ? JSON.parse(storedLastClickedMarker)
      : null;

    setLastClickedMarker(
      initialLastClickedMarker
        ? new window.kakao.maps.LatLng(
            initialLastClickedMarker.lat,
            initialLastClickedMarker.lng
          )
        : null
    );

    if (initialLastClickedMarker) {
      map.panTo(initialLastClickedMarker);
    }

    galleriesMarker.forEach((galleryMarker) => {
      createMarker(galleryMarker, map);
    });
  }, [galleriesMarker, lat, lng]);

  return (
    <div>
      <div
        style={{
          width: "100%",
          maxWidth: "560px",
          height: `${mapHeight + 50}px`,
          position: "absolute",
        }}
      >
        <div
          id="map"
          ref={mapContainer}
          style={{
            width: "auto",
            height: `${mapHeight + 50}px`,
          }}
        />
        {selectedMarker && drawerOpen ? (
          <SwipeableEdgeDrawer
            gallery={selectedMarker}
            onClose={() => setDrawerOpen(false)}
          />
        ) : (
          <SwipeableEdgeDrawer />
        )}
      </div>
    </div>
  );
}

export default KakaoMap;
