import { useState, useEffect } from "react";
import { fetchShowTagsId, fetchTagName } from "../../lib/api/Api";

function FetchShowTags(galleriesInfo) {
  const [tagIds, setTagIds] = useState(null);
  const [tags, setTags] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const ids = galleriesInfo.map((galleryInfo) => {
    return galleryInfo.id;
  });
  useEffect(() => {
    const fetchTagsIds = async () => {
      try {
        setError(null);

        const ids = await fetchShowTagsId(id);
        const sortedIds = [
          ...ids
            .filter((item) => item.tag_id === 52)
            .map((item) => item.tag_id),
          ...ids
            .filter((item) => item.tag_id !== 52)
            .map((item) => item.tag_id),
        ];
        setTagIds(sortedIds);
      } catch (e) {
        setError(e);
      }
    };
    fetchTagsIds();
  }, [galleriesInfo]);

  useEffect(() => {
    const fetchTags = async () => {
      try {
        setError(null);
        const fetchedTags = await Promise.all(
          tagIds.map(async (tagId) => {
            return fetchTagName(tagId);
          })
        );
        setTags(fetchedTags);
      } catch (e) {
        setError(e);
      }
    };

    if (tagIds && tagIds.length > 0) {
      fetchTags()
        .then(() => setLoading(false))
        .catch((error) => setError(error));
    }
  }, [tagIds]);

  return tags;
}

export default FetchShowTags;
