import * as React from "react";
import { useState } from "react";
import PropTypes from "prop-types";
import dayjs from "dayjs";
import isBetweenPlugin from "dayjs/plugin/isBetween";
import { styled } from "@mui/material/styles";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DateCalendar } from "@mui/x-date-pickers/DateCalendar";
import { PickersDay } from "@mui/x-date-pickers/PickersDay";
import { DemoContainer, DemoItem } from "@mui/x-date-pickers/internals/demo";

dayjs.extend(isBetweenPlugin);

const CustomPickersDay = styled(PickersDay, {
  shouldForwardProp: (prop) =>
    prop !== "dayIsBetween" && prop !== "isFirstDay" && prop !== "isLastDay",
})(({ theme, dayIsBetween, isFirstDay, isLastDay }) => ({
  ...(dayIsBetween && {
    borderRadius: "0%",
    backgroundColor: "red",
    color: "white",
    "&:hover, &:focus": {
      backgroundColor: theme.palette.primary.dark,
    },
  }),
  ...(isFirstDay && {
    borderTopLeftRadius: "50%",
    borderBottomLeftRadius: "50%",
    backgroundColor: "white",
    color: "black",
  }),
  ...(isLastDay && {
    borderTopRightRadius: "50%",
    borderBottomRightRadius: "50%",
    backgroundColor: "white",
    color: "black",
  }),
}));

function Day(props) {
  const { day, startDay, endDay, ...other } = props;

  if (startDay == null || endDay == null) {
    return <PickersDay day={day} {...other} />;
  }

  const dayIsBetween = day.isBetween(startDay, endDay, null, "[]");
  const isFirstDay = day.isSame(startDay, "day");
  const isLastDay = day.isSame(endDay, "day");

  return (
    <CustomPickersDay
      {...other}
      day={day}
      disableMargin
      dayIsBetween={dayIsBetween}
      isFirstDay={isFirstDay}
      isLastDay={isLastDay}
    />
  );
}

Day.propTypes = {
  day: PropTypes.object.isRequired,
  selectedDay: PropTypes.object,
};

export default function MyCalendar({ date, color }) {
  const dateArray = date.split(" ~ ");
  const [today, setToday] = useState(dayjs(new Date()));
  const [startDay, setStartDay] = useState(dayjs(dateArray[0]));
  const [endDay, setEndDay] = useState(dayjs(dateArray[1]));
  console.log(startDay);
  console.log(endDay);
  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <DateCalendar
        value={today}
        slots={{ day: Day }}
        slotProps={{
          day: {
            startDay: startDay,
            endDay: endDay,
          },
        }}
        readOnly
      />
    </LocalizationProvider>
  );
}
