import * as React from "react";
import { useState } from "react";
import PropTypes from "prop-types";
import dayjs from "dayjs";
import isBetweenPlugin from "dayjs/plugin/isBetween";
import { styled } from "@mui/material/styles";
import { AdapterDayjs } from "@mui/x-date-pickers/AdapterDayjs";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { DateCalendar } from "@mui/x-date-pickers/DateCalendar";
import { PickersDay } from "@mui/x-date-pickers/PickersDay";
import { DemoContainer, DemoItem } from "@mui/x-date-pickers/internals/demo";

dayjs.extend(isBetweenPlugin);

const CustomPickersDay = styled(PickersDay, {
  shouldForwardProp: (prop) =>
    prop !== "dayIsBetween" && prop !== "isFirstDay" && prop !== "isLastDay",
})(({ theme, dayIsBetween, isFirstDay, isLastDay }) => ({
  ...(dayIsBetween && {
    borderRadius: 0,
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.common.white,
    "&:hover, &:focus": {
      backgroundColor: theme.palette.primary.dark,
    },
  }),
  ...(isFirstDay && {
    borderTopLeftRadius: "25%",
    borderBottomLeftRadius: "0%",
  }),
  ...(isLastDay && {
    borderTopRightRadius: "0%",
    borderBottomRightRadius: "25%",
  }),
}));

function Day(props) {
  const { day, selectedDay, ...other } = props;

  if (selectedDay == null) {
    return <PickersDay day={day} {...other} />;
  }

  const start = selectedDay.startOf("week");
  console.log(start);
  const end = selectedDay.endOf("week");

  const dayIsBetween = day.isBetween(start, end, null, "[]");
  const isFirstDay = day.isSame(start, "day");
  const isLastDay = day.isSame(end, "day");

  return (
    <CustomPickersDay
      {...other}
      day={day}
      disableMargin
      dayIsBetween={dayIsBetween}
      isFirstDay={isFirstDay}
      isLastDay={isLastDay}
    />
  );
}

Day.propTypes = {
  day: PropTypes.object.isRequired,
  selectedDay: PropTypes.object,
};

export default function MyCalendar({ date, color }) {
  const dateArray = date.split(" ~ ");
  const [today, setToday] = useState(dayjs(new Date()));
  const [startDay, setStartDay] = useState(dayjs(dateArray));
  const [endDay, setEndDay] = useState(dayjs(dateArray));

  return (
    <LocalizationProvider dateAdapter={AdapterDayjs}>
      <DateCalendar
        value={today}
        slots={{ day: Day }}
        slotProps={{
          day: {
            startDay: startDay,
            endDay: endDay,
          },
        }}
        readOnly
      />
    </LocalizationProvider>
  );
}
