/*global kakao*/
import React, { useEffect } from "react";
import Marker from "./Marker";
import { MapMarker } from "react-kakao-maps-sdk";
import icon from "../../lib/icon/loc7.png";

function KakaoMap({ state, showMarker }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const test = Marker();
  console.log(test);

  useEffect(() => {
    const container = document.getElementById("map");
    const options = {
      center: new kakao.maps.LatLng(state.lat, state.lng),
      level: 3,
    };

    const map = new kakao.maps.Map(container, options);

    if (showMarker) {
      const markerPosition = new kakao.maps.LatLng(state.lat, state.lng);
      const marker = new kakao.maps.Marker({
        position: markerPosition,
      });
      marker.setMap(map);
    }
  }, [state, showMarker]);

  return (
    <div>
      <div id="map" style={{ width: "100%", height: `${mapHeight}px` }}></div>
    </div>
  );
}

export default React.memo(KakaoMap);
