import * as React from "react";
import Done from "@mui/icons-material/Done";
import styled from "styled-components";

import { era, genre, takePhoto, artMovement, orient } from "./Tags";
import TagsCheckBox from "./TagsCheckBox";
import HorizontalLine from "../common/HorizontalLine";

const Box = styled.div`
  algin-item: left;
  width: 95%;

  padding: 0.4rem 0 0 1.1rem;
  margin: 0 auto;
  padding: 5px 5px 30px 5px;
  p{
    font-size: 1.4rem;
    color:white;
    padding-bottom: 0.4rem;
    margin 0 0 0 1.1rem;
  }
`;

function HashTag() {
  const [takePhotoValue, setTakePhotoValue] = useState([]);
  const [orientValue, setOrientValue] = useState([]);
  const [genreValue, setGenreValue] = useState([]);
  const [eraValue, setEraValue] = useState([]);
  const [artMovementValue, setArtMovementValue] = useState([]);

  const handleUnselectAll = () => {
    setTakePhotoValue([]);
    setOrientValue([]);
    setGenreValue([]);
    setEraValue([]);
    setArtMovementValue([]);
  };

  return (
    <>
      <Box>
        <HorizontalLine />
        <p>촬영 가능 여부</p>
        <TagsCheckBox
          items={takePhoto}
          value={takePhotoValue}
          setValue={setTakePhotoValue}
        />
        <HorizontalLine />
        <p>지역별</p>
        <TagsCheckBox
          items={orient}
          value={orientValue}
          setValue={setOrientValue}
        />
        <HorizontalLine />
        <p>장르별</p>
        <TagsCheckBox
          items={genre}
          value={genreValue}
          setValue={setGenreValue}
        />
        <HorizontalLine />
        <p>시대별</p>
        <TagsCheckBox items={era} value={eraValue} setValue={setEraValue} />
        <HorizontalLine />
        <p>미술사조</p>
        <TagsCheckBox
          items={artMovement}
          value={artMovementValue}
          setValue={setArtMovementValue}
        />
        <HorizontalLine />
        <button onClick={handleUnselectAll}>모두 선택 해제</button>
      </Box>
    </>
  );
}

export default HashTag;
