/*global kakao*/
import React, { useEffect, useRef } from "react";

function KakaoMap({ lat, lng, showMarker }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const mapRef = useRef(null);

  useEffect(() => {
    if (!mapRef.current) return;

    const options = {
      center: new kakao.maps.LatLng(lat, lng),
      level: 3,
    };

    const map = new kakao.maps.Map(mapRef.current, options);

    if (showMarker) {
      const markerPosition = new kakao.maps.LatLng(lat, lng);
      const marker = new kakao.maps.Marker({
        position: markerPosition,
      });
      marker.setMap(map);
    }

    // Add additional markers
    const positions = [
      {
        content: "<div>카카오</div>",
        latlng: new kakao.maps.LatLng(33.450705, 126.570677),
      },
      {
        content: "<div>생태연못</div>",
        latlng: new kakao.maps.LatLng(33.450936, 126.569477),
      },
      {
        content: "<div>텃밭</div>",
        latlng: new kakao.maps.LatLng(33.450879, 126.56994),
      },
      {
        content: "<div>근린공원</div>",
        latlng: new kakao.maps.LatLng(33.451393, 126.570738),
      },
    ];

    for (var i = 0; i < positions.length; i++) {
      const position = positions[i];
      const marker = new kakao.maps.Marker({
        map: map,
        position: position.latlng,
      });

      const infowindow = new kakao.maps.InfoWindow({
        content: position.content,
      });

      kakao.maps.event.addListener(
        marker,
        "mouseover",
        makeOverListener(map, marker, infowindow)
      );
      kakao.maps.event.addListener(
        marker,
        "mouseout",
        makeOutListener(infowindow)
      );
    }

    // Clean up markers when the component unmounts
    return () => {
      kakao.maps.event.clearListeners(map, "mouseover");
      kakao.maps.event.clearListeners(map, "mouseout");
    };
  }, [lat, lng, showMarker]);

  // Function to add the InfoWindow
  function makeOverListener(map, marker, infowindow) {
    return function () {
      infowindow.open(map, marker);
    };
  }

  function makeOutListener(infowindow) {
    return function () {
      infowindow.close();
    };
  }

  return (
    <div>
      <div
        ref={mapRef}
        style={{ width: "100%", height: `${mapHeight}px` }}
      ></div>
    </div>
  );
}

export default React.memo(KakaoMap);
