import styled from "styled-components";
import InstagramIcon from "@mui/icons-material/Instagram";
import SentimentSatisfiedAltIcon from "@mui/icons-material/SentimentSatisfiedAlt";
import { Link } from "react-router-dom";

const FootBox = styled.div`
  bottom: 0;
  width: 100%;
  background: black;
`;

const Foot = styled.div`
  justify-content: center;
  align-items: center;
  color: white;
  padding-left: 0.8rem;
  padding-bottom: 0.4rem;
  font-family: "Open Sans", sans-serif;
  font-weight: bold;
  p {
    margin: 0;
    font-size: 0.8rem;
    Link {
      display: flex;
      alignitems: center;
    }
    svg {
      transform: translateY(3px) translateX(3px);
      font-size: 1.2rem;
      margin-right: 0.2rem;
      color: #f04465;
      padding-right: 0;
    }
    span {
      color: #ffffff;
    }
    .name {
      color: #f04465;
    }
  }
`;

const HrLine = styled.hr`
  width: 96%;
  border: none;
  border-top: 1px solid #868686; /* change the color to blue and thickness to 2px */
`;

const Footer = () => {
  function handleInstagramSearch() {
    const url = "instagram://user?username=exhi_lover";

    window.open(url, `_blank`);
    setTimeout(() => {
      window.open(`https://www.instagram.com/exhi_lover/`, "_blank");
    }, 100);
  }
  return (
    <FootBox>
      <HrLine />
      <Foot>
        <p>
          2023 Exhi-lover published. Art is Life. Life is Art. <br />
          MADE BY{" "}
          <Link to="https://www.instagram.com/exhi_lover/" target="_blank">
            <InstagramIcon />
            <span className="name"> EXHI_LOVER</span>
          </Link>
        </p>
      </Foot>
    </FootBox>
  );
};

export default Footer;
