import React, { useEffect, useState } from "react";
import styled from "styled-components";
import line from "../../lib/icon/line.png";
import FetchShowsInfo from "./FetchShowsInfo";
import GlobalStyle from "../../lib/styles/fontStyle";

const Drawer = styled.div`
  position: absolute;
  display: flex;
  flex-direction: column; /* Stack elements vertically */
  justify-content: flex-start; /* Stick to the top */
  align-items: center; /* Center horizontally */
  padding-top: 10px;
  padding-right: 14px;
  padding-left: 14px;
  height: ${(props) => props.height}px;
  max-height: ${(props) => props.height}px;
  //   height: 200px;
  width: 100%;
  background: #1e1e1e;
  border-radius: 15px 15px 0 0;
  bottom: 0;
  z-index: 999;
`;

const Container = styled.div`
  background: #e2e2e2;
  // justify-content: flex-start; /* Stick to the top */
  flex-direction: column; /* Stack elements vertically */
  width: 100%;
  margin: 10px 10px 3px 10px;
  padding: 10px 10px 0 10px;
  // display: flex;
  flex: 1;
  justify-content: center;
  border-radius: 10px 10px 0 0;

  font-size: 20px;
  font-style: normal;
  font-weight: 700;
  line-height: normal;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const Content = styled.div`
  // flex: 1;
  display: flex;
  // justify-content: center;
  flex-direction: column;
  flex-direction: row; /* Stack elements vertically */
  margin-top: 4px;
  margin-bottom: 5px;
  height: 128px;
  // padding: 20px; /* Apply padding here */
  background: white;

  font-size: 18px;
  img {
    position: relative;
    top: 4px;
    margin-left: 20px;
    width: 84px;
    height: 120px;
  }
`;

const ScrollBox = styled.div`
  overflow-y: scroll;
  height: 150px;
`;

const Inform = styled.div`
  height: 128px;
  position: relative;
  margin-left: 16px;
  margin-right: 16px;
  width: calc(100% - 120px); /* Adjust the width as needed */
  overflow: hidden;
  //   white-space: nowrap;

  .mapShowName {
    margin-top: 14px;

    display: -webkit-box;
    -webkit-line-clamp: 2; /* Number of lines */
    -webkit-box-orient: vertical;

    color: #000;
    font-size: 18px;
    font-style: normal;
    font-weight: 700;
    line-height: normal;

    text-overflow: ellipsis;
    overflow: hidden;
  }
  .mapShowArtist {
    margin-top: 3px;

    display: -webkit-box;
    -webkit-line-clamp: 1; /* Number of lines */
    -webkit-box-orient: vertical;

    color: #4b4b4b;
    font-size: 15px;
    font-style: normal;
    font-weight: 300;
    line-height: normal;
    text-overflow: ellipsis;
    overflow: hidden;
  }
  .mapShowTag {
    position: absolute;
    bottom: 31px;

    display: -webkit-box;
    -webkit-line-clamp: 2; /* Number of lines */
    -webkit-box-orient: vertical;

    color: #b7afaf;
    font-size: 13px;
    font-style: normal;
    font-weight: 400;
    line-height: normal;
    text-overflow: ellipsis;
    overflow: hidden;
  }
  .mapShowTerm {
    position: absolute;
    bottom: 10px;

    color: #232222;
    font-size: 14px;
    font-style: normal;
    font-weight: 400;
    line-height: normal;
  }
`;

const SwipeableEdgeDrawer = ({ gallery }) => {
  const [currentHeight, setCurrentHeight] = useState(100);
  const galleryInfo = FetchShowsInfo(gallery?.id);
  //   const [galleryInfo, setGalleryInfo] = useState();
  useEffect(() => {
    if (gallery !== undefined) {
      setCurrentHeight(200);
      //   setGalleryInfo(FetchShowsInfo(gallery.id));
    } else {
      setCurrentHeight(20);
    }
  }, [gallery]);

  return (
    <>
      <GlobalStyle />
      <Drawer currentHeight={currentHeight}>
        <img src={line} alt="Line" style={{ width: "120px", height: "5px" }} />
        {gallery ? (
          <Container>
            {gallery.title}
            <ScrollBox>
              {galleryInfo
                ? galleryInfo.map((show, index) => {
                    const imgSrc =
                      "/upload/shows/" +
                      show.show_place_eng +
                      "/" +
                      show.id +
                      "/1.png";
                    return (
                      <Content key={index}>
                        <img src={imgSrc} alt="" />
                        <Inform>
                          <div className="mapShowName">{show.show_name}</div>
                          {/* <div className="mapShowArtist">
                            {show.show_artist}
                          </div> */}
                          <div className="mapShowTag">{show.show_artist}</div>
                          <div className="mapShowTerm">{show.show_term}</div>
                        </Inform>
                      </Content>
                    );
                  })
                : "LOADING..."}
            </ScrollBox>
          </Container>
        ) : (
          <div></div>
        )}
      </Drawer>
    </>
  );
};

export default React.memo(SwipeableEdgeDrawer);
