import * as React from "react";
import Done from "@mui/icons-material/Done";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  padding: 5px 5px 30px 5px;
  width: 95%;
  margin: 0 auto;
  text-align: left;
  line-height: 1.2;
  padding: 0.4rem 0 0 1.1rem;
  background-color: #000000;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const Item = styled.div`
  display: flex;
  align-items: center;
  font-size: 0.95rem;
  font-weight: bold;
  margin-right: 0.45rem;
  margin-bottom: 10px;
  border-radius: 11px;
  padding: 3px 8px;
  background-color: ${({ checked }) => (checked ? "white" : "black")};
  color: ${({ checked }) => (checked ? "black" : "white")};
  border: ${({ checked }) =>
    checked ? "0.11rem solid black" : "0.03rem solid white"};
  cursor: pointer;
  svg {
    font-size: 1.1rem;
    color: black;
    margin-right: 0px;
  }
`;

const TagsCheckBox = ({ items }) => {
  const [value, setValue] = React.useState([]);

  const handleItemClick = (item) => {
    if (value.includes(item)) {
      setValue((val) => val.filter((text) => text !== item));
    } else {
      setValue((val) => [...val, item]);
    }
  };

  return (
    <Container>
      {items.map((item) => (
        <Item
          key={item}
          checked={value.includes(item)}
          onClick={() => handleItemClick(item)}
        >
          {value.includes(item) && <Done />}
          {item}
        </Item>
      ))}
    </Container>
  );
};

export default TagsCheckBox;
