import { useState } from "react";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardCover from "@mui/joy/CardCover";
import Typography from "@mui/joy/Typography";
import Link from "@mui/joy/Link";
import { TableRow, TableCell, createTheme, ThemeProvider } from "@mui/material";
import Button from "@mui/joy/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import Dialog from "@mui/material/Dialog";
import Detail from "./Detail";

function Post({ show_name, show_place, show_place_eng, show_artist }) {
  const imgSrc = "/upload/" + show_place_eng + "/seoul/" + show_name + "/1.png";
  const [open, setOpen] = useState(false);
  const handleClose = () => {
    setOpen(false);
  };
  const handleClickOpen = () => {
    setOpen(true);
  };

  return (
    <Card
      sx={{
        //minWidth: 150,
        //width: "30ch"
        width: "95%",
        height: "auto",
        margin: "auto",
        marginTop: "20px",
        bgcolor: "#131313", //initial
        boxShadow: "none",
        "--Card-padding": "8px",
        // post.js 영역 넓이
      }}
    >
      <Button onClick={handleClickOpen} sx={{ margin: "auto", width: "100%" }}>
        <Box
          sx={{
            position: "relative", //이미지
            margin: "auto",
            marginTop: "10px",
            width: "100%",
            backgroundColor: "primary.dark",
            "&:hover": {
              backgroundColor: "primary.main",
              opacity: [0.9, 0.8, 0.7],
            },
          }}
        >
          <Box
            component="img"
            sx={{
              width: "100%",
              margin: "auto", //박스를 쓰지말고 img 태그를 사용하면서 alt, width, height 설정해주고
            }}
            src={imgSrc}
          />

          <CardCover
            className="gradient-cover"
            sx={{
              "&:hover, &:focus-within": {
                opacity: 1,
              },
              //opacity: 0, 이걸 넣으면 마우스를 올리거나 클리하면 생기고 때면 사라짐. 즉 나한테 필요 없뜸

              paddingRight: "10px",
            }}
          >
            {/* The first box acts as a container that inherits style from the CardCover */}
          </CardCover>
        </Box>
      </Button>
      {/* 미술관 팝업 시작 */}
      <Button onClick={handleClickOpen}>
        <Box
          sx={{
            width: "100%",
            display: "flex",
            gap: 1,
            mt: 1.5,
            alignItems: "center",
            paddingBottom: "10px",
          }}
        >
          <Box sx={{ width: "9%", paddingRight: "2px" }}>
            <Avatar
              src="https://images.unsplash.com/profile-1502669002421-a8d274ad2897?dpr=2&auto=format&fit=crop&w=32&h=32&q=60&crop=faces&bg=fff"
              size="sm"
              sx={{ "--Avatar-size": "1.5rem" }}
            />
          </Box>
          <Box>
            <Typography
              textAlign={"left"}
              sx={{
                overflow: "hidden",
                display: "block",
                width: "24ch",
                textOverflow: "ellipsis",
                color: "#dbdbdb",
              }}
            >
              {show_place}
            </Typography>
          </Box>
        </Box>
      </Button>
      {/* 미술관 팝업 끝 */}
      {/* 전시 팝업 시작 */}
      <Button onClick={handleClickOpen}>
        <Box marginBottom={"5px"} sx={{ width: "100%" }}>
          <Typography
            textAlign={"left"}
            sx={{
              margin: "auto",
              paddingBottom: "3px",
              overflow: "hidden",
              textOverflow: "ellipsis",
              color: "#eeeeee",
            }}
          >
            {show_name}
          </Typography>
          <Typography
            noWrap
            textAlign={"left"}
            sx={{
              margin: "left",
              overflow: "hidden",
              textOverflow: "ellipsis",
              color: "#eeeeee",
              paddingTop: "1px",
            }}
          >
            {show_artist}
          </Typography>
        </Box>
      </Button>
      {/* 전시 팝업 끝 */}
      <Dialog
        fullWidth={"xl"}
        maxWidth={"xl"}
        open={open}
        onClose={handleClose}
      >
        <Detail />
      </Dialog>
    </Card>
  );
}

export default Post;
