import Calendar from "react-calendar";
import "./Calendar.css"; // css import

import React, { Component } from "react";
import { useState } from "react";

function MyCalendar({ date }) {
  const [value, onChange] = useState(new Date());
  const dateArray = date.split(" ~ ");
  console.log(dateArray);

  const [dateObjects, setDateObjects] = useState([]);
  const createDateObjects = () => {
    const newDateObjects = dateArray.map((date) => new Date(date));
    setDateObjects(newDateObjects);
  };
  return (
    <>
      <div className="calendar-container">
        <Calendar onChange={onChange} value={value} selectRange={true} />
      </div>
    </>
  );
}

export default MyCalendar;
