import styled from "styled-components";
import GlobalStyle from "../../lib/styles/fontStyle";
import { Link } from "react-router-dom";
import FileDownloadDoneSharpIcon from "@mui/icons-material/FileDownloadDoneSharp";
import HorizontalLine from "../common/HorizontalLine";
import LocationOnSharpIcon from "@mui/icons-material/LocationOnSharp";
import Person2SharpIcon from "@mui/icons-material/Person2Sharp";
import CalendarMonthIcon from "@mui/icons-material/CalendarMonth";
import ShareIcon from "@mui/icons-material/Share";
import AccountBalanceIcon from "@mui/icons-material/AccountBalance";
import PhoneIcon from "@mui/icons-material/Phone";
import MiniMap from "../map/MiniMap";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { useEffect, useState } from "react";
import MyCalendar from "../common/MyCalendar";
import { fetchGalleryInfo } from "../../lib/api/api";

const DetailViewerBlock = styled.div`
  width: 100%;
  Booking {
    display: flex;
    justify-content: center;
    align-items: center;
    color: black;
  }
`;

const MainImage = styled.img`
  padding-left: 0.8rem;
  width: 100%;
  aspect-ratio: 16/9; /* change to the desired aspect ratio */
  object-fit: cover;
  overflow: hidden;
  display: flex;
  align-items: center;
  border-bottom: 0.03rem solid #202020;
`;

const SliderWrapper = styled.div`
  .slick-dots {
    display: inline-block;
    vertical-align: middle;
    margin: auto 0;
    padding: 0;
  }

  .slick-dots li {
    list-style: none;
    cursor: pointer;
    display: inline-block;
    margin: 0 0;
  }

  .slick-dots li button {
    margin-top: 0.5rem;
    border: none;
    background: #b0b0b0;
    color: transparent;
    cursor: pointer;
    display: block;
    height: 5px;
    width: 5px;
    border-radius: 100%;
    padding: 0;
  }
  .slick-dots li.slick-active button {
    background-color: ${(props) => props.color || "#ec6c03"};
  }
  .slick-arrow {
    display: none !important;
  }
`;

const MainTitle = styled.div`
  margin-top: 0.5rem;
  padding-left: 0.8rem;
  color: white;
  padding-top: 1.2rem;
  p {
    font-size: 1.5rem;
    font-family: "Happiness-Sans-Bold", sans-serif;
    margin: 0.4rem 0;
  }
  strong {
    font-size: 1.8rem;
    color: ${(props) => props.color || "#ec6c03"};
    font-family: "Happiness-Sans-Title", sans-serif;
  }
`;

const Term = styled.div`
  padding-left: 0.8rem;
  color: white;
  padding-top: 1.1rem;
  font-size: 0.83rem;
`;

const BookingArea = styled.div`
  margin-top: 0.45rem;
  margin-bottom: 0.5rem;
`;
const Booking = styled.div`
  background: ${(props) => props.color || "#ec6c03"};
  width: 93%;
  height: 2.8rem;
  color: #000000;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: auto;
  padding-right: 1.7rem;
  p {
    padding-left: 1rem;
    font-size: 1.1rem;
    font-family: "Happiness-Sans-Bold";
  }
  svg {
    // margin-left: 1rem; /* add margin to the left side */
    font-size: 1.6rem;
  }
  &:hover {
    color: white;
    background: #000000;
    border: 1px solid #ffffff;
  }
`;

const SharingArea = styled.div`
  margin-top: 0.45rem;
  margin-bottom: 0.6rem;
`;
const Sharing = styled.div`
  background: ${(props) => props.color || "#ec6c03"};
  width: 93%;
  height: 2.8rem;
  color: #000000;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin: auto;
  padding-right: 1.7rem;
  p {
    padding-left: 1rem;
    font-size: 1.1rem;
    font-family: "Happiness-Sans-Bold";
  }
  svg {
    // margin-left: 1rem; /* add margin to the left side */
    font-size: 1.4rem;
  }
  &:hover {
    color: white;
    background: #000000;
    border: 1px solid #ffffff;
  }
`;
const InfoBlock = styled.div`
  width: 100%;
`;

const Artist = styled.div`
  padding-left: 0.8rem;
  padding-right: 0.8rem;
  color: white;
  font-family: "Happiness-Sans-Bold";
  font-size: 1rem;
  line-height: 1.4;
  p {
    font-size: 1.5rem;
    margin: 0 0 0.3rem 0;
    font-family: "Happiness-Sans-Title";
    svg {
      transform: translateY(5px);
      font-size: 1.6rem;
      margin-right: 0.3rem;
    }
  }
`;
const PlaceInfo = styled.div`
  padding-left: 0.8rem;
  padding-right: 0.8rem;
  padding-bottom: 1rem;
  color: white;
  font-family: "Happiness-Sans-Bold";
  font-size: 1rem;
  line-height: 1.4;
  p {
    font-size: 1.5rem;
    margin: 0 0 0.3rem 0;
    font-family: "Happiness-Sans-Title";
    svg {
      transform: translateY(5px);
      font-size: 1.6rem;
      margin-right: 0.3rem;
    }
  }
`;

const PlaceMap = styled.div`
  margin-top: 0.45rem;
`;

const OpeningTime = styled.div`
  font-family: "Happiness-Sans-Bold";
  padding-left: 0.8rem;
  padding-right: 0.8rem;
  color: white;
  font-size: 1rem;
  line-height: 1.4;
  p {
    font-size: 1.5rem;
    margin: 0 0 0.3rem 0;
    font-family: "Happiness-Sans-Title";
    svg {
      transform: translateY(5px);
      font-size: 1.6rem;
      margin-right: 0.3rem;
    }
  }
  h4 {
    margin: 0;
  }
`;

const ShowPrice = styled.div`
  padding-top: 0rem;
  font-family: "Happiness-Sans-Bold";
  padding-left: 1.1rem;
  color: white;
  font-size: 1.1rem;
  color: white;
`;

const Tags = styled.div`
  margin-top: 0.6rem;
  padding-left: 0.95rem;
  color: white;
  div {
    display: inline;
    margin-right: 6px;
    font-size: 0.97rem;
    &: hover {
