const DetailViewer = ({ show, color, tags }) => {
  const [error, setError] = useState(null);
  const [galleryInfo, setGalleryInfo] = useState(null);
  const [latitude, setLatitude] = useState(null);
  const [longitude, setLongitude] = useState(null);
  const [value, onChange] = useState(new Date());

  const {
    id,
    show_name,
    show_artist,
    show_search,
    show_term,
    show_city,
    gallery,
    show_place,
    show_place_detail,
    show_place_eng,
    show_price,
    show_link,
    show_imgs,
    show_brief,
  } = show[0];

  useEffect(() => {
    const fetchGalleries = async () => {
      try {
        setError(null);
        const data = await fetchGalleryInfo(gallery);
        setGalleryInfo(data);
      } catch (e) {
        setError(e);
      }
    };
    fetchGalleries();
  }, [gallery]);

  let gallery_add_word = "";
  let gallery_phone_num = "";
  let business_hours = "";
  let business_week = "";
  let openingHoursObject;

  if (galleryInfo !== null) {
    gallery_add_word = galleryInfo.gallery_add_word;
    gallery_phone_num = galleryInfo.gallery_phone_num;
    business_hours = galleryInfo.business_hours;
    business_week = galleryInfo.business_week;
    openingHoursObject = JSON.parse(business_hours);
  }

  function handlePhoneCall() {
    window.location.href = `tel:${gallery_phone_num}`;
  }

  useEffect(() => {
    if (galleryInfo !== null) {
      const [lat, lng] = galleryInfo.gallery_add_tude
        .split(/[, ]+/)
        .map(parseFloat);
      setLatitude(lat);
      setLongitude(lng);
    }
  }, [galleryInfo]);

  const images = [];
  for (let i = 1; i <= show_imgs; i++) {
    const imgSrces = `/upload/seoul/${show_place_eng}/${show_name}/${i}.png`;
    images.push(imgSrces);
  }

  const settings = {
    dots: true, // add dots as image indicators
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };
  const handleCopyClipBoard = async (text) => {
    try {
      await navigator.clipboard.writeText(text);

      alert("url 복사 되었습니다");
    } catch (error) {
      alert("url 복사 실패 했습니다");
    }
  };
  return (
    <>
      <GlobalStyle />
      <DetailViewerBlock>
        <SliderWrapper color={color}>
          <Slider {...settings}>
            {images.map((image, index) => (
              <MainImage key={index} src={image} alt="" loading="lazy" />
            ))}
          </Slider>
        </SliderWrapper>
        <MainTitle color={color}>
          <p>{show_place}</p>
          <strong>{show_name}</strong>
        </MainTitle>
        <Tags>
          {tags &&
            tags[id]
              .sort((a, b) =>
                a.includes("사진촬영 가능")
                  ? -1
                  : b.includes("사진촬영 가능")
                  ? 1
                  : 0
              )
              .map((tag, index) => {
                return <div key={index}>#{tag}</div>;
              })}
        </Tags>
        <Term>{show_term}</Term>
        <HorizontalLine />
        <Link to={show_link} target="_blank">
          <BookingArea>
            <Booking color={color}>
              <p>예약하기</p>
              <FileDownloadDoneSharpIcon />
            </Booking>
          </BookingArea>
        </Link>
        <div
          onClick={() =>
            handleCopyClipBoard(decodeURIComponent(window.location.href))
          }
        >
          <SharingArea>
            <Sharing color={color}>
              <p>공유하기</p>
              <ShareIcon />
            </Sharing>
          </SharingArea>
        </div>
        <ShowPrice>
          {show_price ? `${show_price}원` : "본 전시는 무료입니다."}
        </ShowPrice>
        <HorizontalLine />
        <InfoBlock>
          <Artist>
            <p>
              <Person2SharpIcon />
              참여 작가
            </p>
            {show_artist}
          </Artist>
          <HorizontalLine />
          <OpeningTime>
            <p>
              <CalendarMonthIcon /> {show_term}
            </p>{" "}
            <h4>휴무일: {business_week}</h4>
            <MyCalendar date={show_term} />
          </OpeningTime>

          <HorizontalLine />
          <GalleryInformation>
            <p>
              <AccountBalanceIcon />
              전시 시간
            </p>
            {openingHoursObject && (
              <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
                {Object.entries(openingHoursObject).map(([day, hours]) => (
                  <li key={day}>
                    {day}: {hours}
                  </li>
                ))}
              </ul>
            )}
          </GalleryInformation>

          <HorizontalLine />
          <PhoneNum>
            <p>
              <PhoneIcon />{" "}
              <a href={`tel:${gallery_phone_num}`} onClick={handlePhoneCall}>
                {gallery_phone_num}
              </a>
            </p>
          </PhoneNum>
          <HorizontalLine />
          <PlaceInfo>
            <p>
              <LocationOnSharpIcon />
              {show_place}
            </p>
            {gallery_add_word}
            <br />
            {show_place_detail ? show_place_detail : "상세 장소가 없습니다."}
            <PlaceMap>
              {latitude && longitude && (
                <MiniMap name={show_place} lat={latitude} lng={longitude} />
              )}
            </PlaceMap>
          </PlaceInfo>
        </InfoBlock>
      </DetailViewerBlock>
    </>
  );
};

export default DetailViewer;
