import axios from "axios";
import { useState, useEffect } from "react";

function GalleriesTable({ galleryName }) {
  const [error, setError] = useState(null);
  const [gallery, setGallery] = useState(null);
  useEffect(() => {
    const fetchGalleries = async () => {
      try {
        setError(null);
        const url = `/api/gallery/${galleryName}`;
        // const response = await axios.get(`https://exhi-lover.com${url}`)
        const response = await axios.get(`https://localhost:5100${url}`);
        setGallery(response.data);
      } catch (e) {
        setError(e);
      }
    };
  }, []);
  return { gallery, error };
}

export default GalleriesTable;
