import Calendar from "react-calendar";
import "./Calendar.css"; // css import

import React, { Component } from "react";
import { useState } from "react";

function MyCalendar({ date }) {
  const [value, onChange] = useState(new Date());
  const dateArray = date.split(" ~ ");

  const parsedDates = dateArray.map((dateString) => {
    const timestamp = Date.parse(dateString);
    return new Date(timestamp);
  });
  const [duringDate, setDuringDate] = useState(parsedDates);
  const currentDate = new Date();
  const oneYearLaterDate = new Date(currentDate);
  oneYearLaterDate.setFullYear(currentDate.getFullYear() + 1);

  return (
    <>
      <div className="calendar-container">
        <Calendar
          defaultValue={duringDate}
          selectRange={true}
          maxDate={oneYearLaterDate} // 오늘 이후 날짜는 허용되지 않음
          minDate={new Date()}
        />
      </div>
    </>
  );
}

export default MyCalendar;
