import { Component, useState } from "react";
import "./App.css";
import logo_orange from "./logo/logo_orange.png";
import Activate from "./component/Activate";
import * as React from "react";

//검색과 지도 선택에 사용될 useState

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      shows: "",
      completed: 0,
      searchKeyword: "",
    };
  }

  componentDidMount() {
    //this.timer = setInterval(this.progress, 20);
    this.callApi()
      .then((res) => this.setState({ shows: res }))
      .catch((err) => console.log(err));
  }

  callApi = async () => {
    const response = await fetch("/api/shows");
    const body = await response.json();
    return body;
  };

  render() {
    return (
      <body className="Body">
        <div className="App">
          <header className="Nav">
            <img src={logo_orange} className="Logo" />
          </header>
          <Activate />
        </div>
      </body>
    );
  }
}

export default App;
