import { create } from "zustand";
import { persist } from "zustand/middleware";

const useBearsStore = create(
  persist(
    (set) => ({
      menuValue: "0",
      setMenuValue: (value) => set({ menuValue: value }),
      buttonValue: "0",
      setButtonValue: (value) => set({ buttonValue: value }),
    }),
    {
      name: "bears-storage", // name of the storage (must be unique)
      getStorage: () => localStorage, // specify localStorage (or sessionStorage if needed)
    }
  )
);

export default useBearsStore;
