import React from "react";
import MainButton from "../component/button/MainButton";
import MainHeader from "../component/header/MainHeader";
import styled from "styled-components";
import GlobalStyle from "../lib/styles/fontStyle";
import Footer from "../component/common/Footer";
import { padding } from "@mui/system";

const MainImage = styled.div`
  width: 100%;
  height: 0;
  padding-bottom: 50.25%;
  position: relative;
  overflow: hidden;
  margin: 0 auto;
  img {
    position: absolute;
    top: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.6s ease;
  }
`;

const InformBox = styled.div`
  background: white;
  padding: 5px 5px 5px 20px;
  position: relative;
  z-index: 1;
`;

const InformTitle = styled.div`
  margin-top: 0.9rem;
  font-family: "Happiness-Sans-Title", sans-serif;
  font-size: 1.9rem;
  padding-bottom: 0.5rem;
`;

const InformContext = styled.div`
  font-family: "Happiness-Sans-Bold", sans-serif;
  font-size: 1.1rem;
  font-weight: bold;
  padding-bottom: 0.4rem;
  p {
    margin: 0.3rem 0; /* add this line to adjust the margin */
  }
`;

function MainPage() {
  const quotes = [
    "Art is not what you see, but what you make others see. - Edgar Degas",
    "Art is the lie that enables us to realize the truth. - Pablo Picasso",
    "The purpose of art is to make the invisible visible. - Paul Klee",
    "Art is not a handicraft, it is the transmission of feeling the artist has experienced. - Leo Tolstoy",
    "Art is the highest form of hope. - Gerhard Richter",
    "Art is the signature of civilizations. - Beverly Sills",
    "Art is the language of the imagination. - James Whistler",
    "Art is not what you create, it's what you leave behind. - W.H. Auden",
    "Art enables us to find ourselves and lose ourselves at the same time. - Thomas Merton",
    "The artist's job is to be a witness to his time in history. - Robert Rauschenberg",
  ];
  const randomIndex = Math.floor(Math.random() * quotes.length);
  const splitQuote = quotes[randomIndex].split("-");
  return (
    <>
      <GlobalStyle />
      <MainHeader />
      <MainImage>
        <img src="/upload/main/3.png" alt="" loading="lazy" />
      </MainImage>
      <InformBox>
        <InformTitle>Welcome to Exhi-lover</InformTitle>
        <InformContext>
          <p>{splitQuote[0]}</p>
          <p
            style={{ textAlign: "right", paddingRight: "13px" }}
          >{`- ${splitQuote[1]}`}</p>
          {/* <p>전시회를 사랑하는 여러분</p>
          <p>검색을 더 편하게 도와드릴께요</p>
          <p>아래 원하시는 버튼을 눌러 검색해보세요</p> */}
          {/* "Art is not what you see, but what you make others see." - Edgar Degas
          "Art is the lie that enables us to realize the truth." - Pablo Picasso
          "The purpose of art is to make the invisible visible." - Paul Klee
          "Art is not a handicraft, it is the transmission of feeling the artist
          has experienced." - Leo Tolstoy "Art is the highest form of hope." -
          Gerhard Richter "Art is the signature of civilizations." - Beverly
          Sills "Art is the language of the imagination." - James Whistler "Art
          is not what you create, it's what you leave behind." - W.H. Auden "Art
          enables us to find ourselves and lose ourselves at the same time." -
          Thomas Merton "The artist's job is to be a witness to his time in
          history." - Robert Rauschenberg */}
        </InformContext>
      </InformBox>
      <MainButton />
      <Footer />
    </>
  );
}

export default React.memo(MainPage);
