import * as React from "react";
import AspectRatio from "@mui/joy/AspectRatio";
import Avatar from "@mui/joy/Avatar";
import Box from "@mui/joy/Box";
import Card from "@mui/joy/Card";
import CardOverflow from "@mui/joy/CardOverflow";
import Link from "@mui/joy/Link";
import IconButton from "@mui/joy/IconButton";
import Input from "@mui/joy/Input";
import Typography from "@mui/joy/Typography";
import MoreHoriz from "@mui/icons-material/MoreHoriz";
import FavoriteBorder from "@mui/icons-material/FavoriteBorder";
import ModeCommentOutlined from "@mui/icons-material/ModeCommentOutlined";
import SendOutlined from "@mui/icons-material/SendOutlined";
import Face from "@mui/icons-material/Face";
import BookmarkBorderRoundedIcon from "@mui/icons-material/BookmarkBorderRounded";
import "./Post.css";

const Post = () => {
  return (
    <Box sx={{ mx: 2.5 }}>
      <Card
        variant="outlined"
        sx={{
          height: "auto",
          maxHeight: 400,
          minWidth: 200,
          maxWidth: 700,
          backgroundColor: "#1b1b1b",
          "--Card-radius": (theme) => theme.vars.radius.xs,
        }}
      >
        <Box>
          <Box sx={{ display: "flex", alignItems: "center", pb: 1.5 }}>
            <Typography fontWeight="lg">MUI</Typography>
          </Box>
          <Box sx={{ display: "flex", alignItems: "center", mx: -1, my: 1 }}>
            <Box
              sx={{
                display: "flex",
                alignItems: "center",
                gap: 0.5,
                mx: "auto",
              }}
            >
              {[...Array(5)].map((_, index) => (
                <Box
                  key={index}
                  sx={{
                    borderRadius: "50%",
                    width: `max(${6 - index}px, 3px)`,
                    height: `max(${6 - index}px, 3px)`,
                    bgcolor:
                      index === 0 ? "primary.solidBg" : "background.level3",
                  }}
                />
              ))}
            </Box>
          </Box>
          <Typography fontSize="sm">
            The React component library you always wanted
          </Typography>
          <div className="toggle"></div>
        </Box>
      </Card>
      <Card>
        <CardOverflow
          sx={{ height: "auto", maxHeight: 400, minWidth: 200, maxWidth: 700 }}
        >
          <AspectRatio>
            <img
              src="/upload/seoul/mmca/감각의 공간, 워치 앤 칠 2.0/1.png"
              alt=""
              loading="lazy"
            />
          </AspectRatio>
        </CardOverflow>
      </Card>
    </Box>
  );
};

export default Post;
