/*global kakao*/
import React, { useEffect, useRef } from "react";
import Marker from "./Marker";
import { MapMarker } from "react-kakao-maps-sdk";
import icon from "../../lib/icon/loc7.png";

function KakaoMap({ lat, lng, showMarker }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const test = Marker();
  const mapContainer = useRef(null);
  const { kakao } = window;

  useEffect(() => {
    // const container = document.getElementById("map");
    const options = {
      center: new kakao.maps.LatLng(lat, lng),
      level: 4,
    };

    const map = new kakao.maps.Map(mapContainer.current, options);

    if (showMarker) {
      const markerPosition = new kakao.maps.LatLng(lat, lng);
      const marker = new kakao.maps.Marker({
        map: map,
        position: markerPosition,
      });
      // marker.setMap(map);
    }

    for (var i = 0; i < test.length; i++) {
      var position = test[i];

      var markerExhi = new kakao.maps.Marker({
        map: map,
        position: new kakao.maps.LatLng(position.lat, position.lng),
      });

      var infowindow = new kakao.maps.InfoWindow({
        context: position.title,
      });

      kakao.maps.event.addListener(
        markerExhi,
        "mouseover",
        makeOverListener(map, markerExhi, infowindow)
      );
      kakao.maps.event.addListener(
        markerExhi,
        "mouseout",
        makeOutListener(infowindow)
      );
    }
  }, [lat, lng, showMarker]);

  function makeOverListener(map, marker, infowindow) {
    return function () {
      infowindow.open(map, marker);
    };
  }

  function makeOutListener(infowindow) {
    return function () {
      infowindow.close();
    };
  }

  return (
    <div>
      <div
        id="map"
        ref={mapContainer}
        style={{ width: "100%", height: `${mapHeight}px` }}
      ></div>
    </div>
  );
}

export default React.memo(KakaoMap);
