/*global kakao*/
import React, { useEffect, useState } from "react";
import Marker from "./Marker";
import { Map, MapMarker } from "react-kakao-maps-sdk";
import AccountBalanceIcon from "@mui/icons-material/AccountBalance";

function KakaoMap({ state, flag }) {
  const mapHeight = window.innerWidth >= 560 ? 560 : window.innerWidth;
  const imageSize = { width: 22, height: 26 };
  const spriteSize = { width: 36, height: 98 };
  const position = { lat: 37.54699, lng: 127.09598 };
  const test = Marker();
  console.log(test);
  const [center, setCenter] = useState();

  useEffect(() => {
    setCenter(new kakao.maps.LatLng(state.lat, state.lng));

    var container = document.getElementById("map");
    var options = {
      center: new kakao.maps.LatLng(state.lat, state.lng),
      level: 3,
    };

    var map = new kakao.maps.Map(container, options);
    var markerPosition = new kakao.maps.LatLng(state.lat, state.lng);

    if (flag === true) {
      var marker = new kakao.maps.Marker({
        position: markerPosition,
      });
      marker.setMap(map);
    }
  }, [state, flag]);

  return (
    <div>
      <div id="map" style={{ width: "100%", height: `${mapHeight}px` }}>
        <Map center={{ center }}>
          <MapMarker position={{ lat: 33.55635, lng: 126.795841 }}>
            <div style={{ color: "#000" }}>Hello World!</div>
          </MapMarker>
        </Map>
        {/* <Map
        center={{ lat: 33.5563, lng: 126.79581 }}
        style={{ width: "100%", height: `${mapHeight}px` }}
      > */}
        {/* <MapMarker
          position={position}
          image={{
            src: AccountBalanceIcon,
            size: imageSize,
            options: {
              spriteSize: spriteSize,
              // spriteOrigin: coffeeOrigin,
            },
          }}
        /> */}
        {/* </Map> */}
      </div>
    </div>
  );
}

export default React.memo(KakaoMap);
