/*global kakao*/
import React, { useState, useEffect } from "react";

function Map({ state, flag }) {
  // const [mapHeight, setMapHeight] = useState(window.innerWidth);

  // const updateMapHeight = () => {
  // setMapHeight(window.innerWidth <= 560 ? window.innerWidth : 560);
  // };
  console.log("width", window.innerWidth);
  const mapHeight = window.innerWidth >= 560 ? window.innerHeight : 560;
  console.log(mapHeight);
  useEffect(() => {
    var container = document.getElementById("map");
    var options = {
      center: new kakao.maps.LatLng(state.lat, state.lng),
      level: 3,
    };

    var map = new kakao.maps.Map(container, options);
    var markerPosition = new kakao.maps.LatLng(state.lat, state.lng);

    if (flag === true) {
      var marker = new kakao.maps.Marker({
        position: markerPosition,
      });
      marker.setMap(map);
    }
  }, [state, flag]);

  return (
    <div>
      <div id="map" style={{ width: "100%", height: `${mapHeight}px` }}></div>
    </div>
  );
}

export default React.memo(Map);
