import React, { useEffect, useState } from "react";
import styled from "styled-components";
import line from "../../lib/icon/line.png";

const Drawer = styled.div`
  display: flex;
  flex-direction: column; /* Stack elements vertically */
  justify-content: flex-start; /* Stick to the top */
  align-items: center; /* Center horizontally */
  padding-top: 10px;
  padding-right: 10px;
  padding-left: 10px;
  padding-bottom: 10px;
  // height: ${(props) => props.height}px;
  height: 200px;
  width: 100%;
  background: black;
  position: absolute;
  border-radius: 15px 15px 0 0;
  bottom: 0;
  z-index: 999;
`;

const Container = styled.div`
  background: #e2e2e2;
  margin-top: 10px;
  margin-left: 10px;
  display: flex;
  flex: 1;
  justify-content: center;
  // align-items: center; //vertically center
`;

const Content = styled.div`
  flex: 1;
  display: flex;
  justify-content: center;
  // align-items: center;
  // padding: 20px; /* Apply padding here */
  background: white;
`;

const Test = ({ open, galleryId }) => {
  const [currentHeight, setCurrentHeight] = useState(100);

  useEffect(() => {
    if (galleryId === undefined) {
      setCurrentHeight(20);
    } else {
      if (open === true) {
        setCurrentHeight(200);
      } else if (open === false) {
        setCurrentHeight(100);
      }
    }
  }, [open, galleryId]);

  return (
    <Drawer height={currentHeight}>
      <img src={line} alt="Line" style={{ width: "120px", height: "5px" }} />
      <Container>
        국립현대미술관
        {/* <Content>Content under the image</Content> */}
      </Container>
    </Drawer>
  );
};

export default Test;
