import React, { useState } from "react";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import Box from "@mui/material/Box";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import Search from "../items/Search";
import HashTag from "../items/HashTag";
import Map from "../items/Map";

const theme = createTheme({
  palette: {
    white: {
      main: "#ffffff",
      contrastText: "#ffffff",
    },
  },
});

function MainButton() {
  const [stat, changeStat] = useState(0);

  const activateTextField = () => {
    changeStat(0);
  };
  const activateHashTag = () => {
    changeStat(1);
  };
  const activateMap = () => {
    changeStat(2);
  };

  return (
    <div>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          marginRight: "10px",
          "& > *": {
            m: 8,
          },
        }}
      >
        <ThemeProvider theme={theme}>
          <ButtonGroup
            size="large"
            variant="text"
            aria-label="text button group"
            color="white"
            sx={{
              paddingRight: "16px",
              paddingTop: "8px",
            }}
          >
            <Button onClick={activateTextField}>Search</Button>
            <Button onClick={activateHashTag}>HashTag</Button>
            <Button onClick={activateMap}> Map</Button>
            /Users/andrew/Desktop/exhi/exhi
          </ButtonGroup>
        </ThemeProvider>
      </Box>
      <div>{stat === 0 && <Search />}</div>
      {/* 

      <div>{stat === 1 && <HashTag />} </div>
      <div>{stat === 2 && <Map />}</div> */}
    </div>
  );
}

export default MainButton;
