import styled from "styled-components";
import InstagramIcon from "@mui/icons-material/Instagram";
import { Link } from "react-router-dom";

const Foot = styled.div`
  color: white;
  padding-left: 0.8rem;
  padding-bottom: 0.4rem;
  font-family: "Open Sans", sans-serif;
  font-weight: bold;
  p {
    margin: 0;
    Link {
      display: flex;
      alignitems: center;
    }
    svg {
      transform: translateY(3px);
      font-size: 1.2rem;
      margin-right: 0.2rem;
    }
  }
`;

const HrLine = styled.hr`
  width: 96%;
  border: none;
  border-top: 1px solid #868686; /* change the color to blue and thickness to 2px */
`;

const Footer = () => {
  return (
    <>
      <HrLine />
      <Foot>
        <p>2023 Exhi-lover published</p>
        <p>
          MADE BY <InstagramIcon />
          <Link to="https://www.instagram.com/exhi_lover/" target="_blank">
            EXHI_LOVER
          </Link>
        </p>
        <p>
          본인의 전시를 추가 하고 싶으시거나 기타 문의는 위 인스타그램 계정엥
          문의를 주시면 감사하겠습니다.
        </p>
      </Foot>
    </>
  );
};

export default Footer;
