import React, { useState, useEffect, useCallback } from "react";
import Select from "@mui/joy/Select";
import Option from "@mui/joy/Option";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import CssBaseline from "@mui/material/CssBaseline";
import PostTemplate from "../post/PostTemplate";
import SearchBar from "../button/SearchBar";
import styled from "styled-components";
import HorizontalLine from "../layout/HorizontalLine";
import { fetchShowsbySearchQuery } from "../../lib/api/Api";

const SearchContainer = styled.div`
  display: flex;
  align-items: center;
`;

const ShowContent = styled.div`
  text-align: left;
  font-size: 40px;
  font-weight: bold;
  // line-height: 1.2px;
  padding: 6px 0 0 18px;
  color: white;
  font-family: "Happiness-Sans-Title", sans-serif;
`;

const DefaultShow = styled.div`
  color: white;
  padding-left: 0.8rem;
  height: 60rem;
  p {
    margin 0;
    margin-bottom: 0.4rem;
    font-size: 1.4rem;
  }
`;

const NoneShow = styled.div`
  color: white;
  padding-left: 0.8rem;
  height: 320px;
  p {
    margin 0;
    margin-bottom: 0.4rem;
    font-size: 1.4rem;
  }
`;

const SelectArea = styled.div`
  margin-left: auto;
  margin-right: 15px;
  margin-top: 10px;
`;

const darkTheme = createTheme({
  palette: {
    mode: "dark",
  },
});

function Search() {
  const [shows, setShows] = useState(null);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");

  const onSearch = useCallback(
    (searchString) => setSearchQuery(searchString),
    []
  );

  useEffect(() => {
    const fetchShows = async () => {
      try {
        setError(null);
        setShows(null);
        const showsData = await fetchShowsbySearchQuery(searchQuery);
        setShows(showsData);
      } catch (e) {
        setError(e);
      }
    };
    fetchShows();
  }, [searchQuery]);

  if (error)
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <NoneShow>
          <p></p>
        </NoneShow>
      </>
    );
  if (!shows)
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <DefaultShow>
          <p></p>
        </DefaultShow>
      </>
    );
  if (shows.length === 0)
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <NoneShow>
          <p>검색 결과에 맞는 전시가 없습니다.</p>
          <p>다른 검색어를 입력해주세요.</p>
        </NoneShow>
      </>
    );
  if (shows.length !== 0) {
    return (
      <>
        <SearchBar onSearch={onSearch} />
        <HorizontalLine key="horizontal-line" />
        <SearchContainer>
          <ShowContent>Exhibitions</ShowContent>
          <SelectArea>
            <Select
              // theme={darkTheme}
              color="neutral"
              defaultValue="1"
              size="sm"
              variant="soft"
            >
              <Option value="1">최신순</Option>
              <Option value="2">마감순</Option>
            </Select>
          </SelectArea>
        </SearchContainer>
        {shows && <PostTemplate shows={shows} />}
      </>
    );
  }
}

export default React.memo(Search);
