/*global kakao*/
import React, { useEffect, useRef, useState } from "react";
// import Marker from "./Marker";
import "./inform.css";
import icon from "../../lib/icon/loc7.png";

function KakaoMap({ lat, lng, showMarker, galleriesMarker }) {
  // const [mapHeight, setMapHeight] = useState(
  //   (window.innerWidth >= 560 ? 560 : window.innerWidth) * 1.1
  // );
  const mapHeight = (window.innerWidth >= 560 ? 560 : window.innerWidth) * 1.1;
  const mapContainer = useRef(null);
  const overlayRef = useRef(null);
  // const galleriesMarker = Marker();
  console.log("galleriesMarker", galleriesMarker);
  const [selectedMarker, setSelectedMarker] = useState(null);

  const imageSize = new window.kakao.maps.Size(35, 35);
  const galleryMarkerImage = new window.kakao.maps.MarkerImage(icon, imageSize);

  const mapOptions = {
    center: new window.kakao.maps.LatLng(lat, lng),
    level: 9,
  };

  useEffect(() => {
    const map = new window.kakao.maps.Map(mapContainer.current, mapOptions);
    if (showMarker) {
      const markerPosition = new window.kakao.maps.LatLng(lat, lng);
      const marker = new window.kakao.maps.Marker({
        map: map,
        position: markerPosition,
      });
    }

    galleriesMarker.forEach((galleryMarker) => {
      const setGalleryMarker = new window.kakao.maps.Marker({
        map: map,
        position: new window.kakao.maps.LatLng(
          galleryMarker.latlng.lat,
          galleryMarker.latlng.lng
        ),
        image: galleryMarkerImage,
      });
      console.log("galleryMarker", galleryMarker);

      window.kakao.maps.event.addListener(
        setGalleryMarker,
        "click",
        function () {
          console.log(1);
          if (!selectedMarker || selectedMarker !== setGalleryMarker) {
            const overlayContent =
              '<div class="wrap">' +
              '   <div class="box">' +
              '     <div class="info">' +
              '       <div class="title">' +
              `         ${galleryMarker.title}` +
              '            <div class="close" onclick="closeOverlay()" title="닫기"></div>' +
              "       </div>" +
              '       <div class="body">' +
              '         <div class="navLeft"></div>' +
              '           <div class="img">' +
              `             <img src="https://t1.daumcdn.net/localimg/localimages/07/mapapidoc/thumnail.png">` +
              "           </div>" +
              '           <div class="desc">' +
              `             <div class="show_name">${galleryMarker.add}123123</div>` +
              `             <div class="show_tags">${galleryMarker.contact}1231 231 2311 23</div>` +
              `             <div class="duration">${galleryMarker.url}</div>` +
              "           </div>" +
              '         <div class="navRight"></div>' +
              "       </div>" +
              "     </div>" +
              "   </div>" +
              "</div>";

            if (!overlayRef.current) {
              overlayRef.current = new window.kakao.maps.CustomOverlay({
                content: overlayContent,
                position: setGalleryMarker.getPosition(),
              });
              overlayRef.current.setMap(map);
            } else {
              overlayRef.current.setPosition(setGalleryMarker.getPosition());
              overlayRef.current.setContent(overlayContent);
            }
            const closeOverlay = () => {
              overlayRef.current.setMap(null);
              setSelectedMarker(null);
              overlayRef.current = null;
              map.panTo(setGalleryMarker.getPosition());
            };
            window.closeOverlay = closeOverlay;
            map.panTo(setGalleryMarker.getPosition());
          }
          setSelectedMarker(setGalleryMarker);
        }
      );
    });

    // function addLayout(galleryMarker) {}
  }, [lat, lng, showMarker, galleriesMarker]);

  return (
    <div>
      <div
        id="map"
        ref={mapContainer}
        style={{ width: "100%", height: `${mapHeight}px` }}
      ></div>
    </div>
  );
}

export default KakaoMap;
