import styled from "styled-components";
import Post from "./Post";
import colorSet from "../../lib/styles/colorSet";
import { useEffect, useState } from "react";
import axios from "axios";

const TemplateBlock = styled.div`
  padding-top: 0.2rem;
  box-sizing: border-box;
  background: #000000;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-flow: row wrap;
`;

const PostTemplate = ({ shows }) => {
  const [tags, setTags] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTags = async () => {
      try {
        setError(null);
        setTags(null);
        const tagsData = await Promise.all(
          shows.map(async (show) => {
            const url = `/api/exhibition_tags/`;
            // const response = await axios.get(`https://exhi-lover.com${url}`);
            const response = await axios.get(`http://localhost:5100${url}`);
            if (!response.ok)
              throw new Error(`Failed to fetch tags for show ID ${show.id}`);
            const data = await response.json();
            return { showId: show.id, tags: data.tags };
          })
        );
      } catch (e) {
        setError(e);
      }
    };
    fetchTags();
  });
  console.log({ shows });
  return (
    <TemplateBlock>
      {shows.map((show, index) => {
        const key = `${show.show_name}`; // create unique key prop value
        const colorIndex = index % colorSet.length; // determine color index based on position in array
        const color = colorSet[colorIndex]; // get color from colorSet array
        return (
          <Post
            show={show}
            key={key}
            color={color}
            tags={tags && tags[show.id]}
          />
        ); // pass color as prop to Post component
      })}
    </TemplateBlock>
  );
};

export default PostTemplate;
