import React, { useState } from "react";
import Button from "@mui/material/Button";
import ButtonGroup from "@mui/material/ButtonGroup";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import { purple } from "@mui/material/colors";

/*
const theme = createTheme({
  palette: {
    primary: {
      main: purple[400],
    },
    secondary: {
      main: "#11cb5f",
    },
  },
});
 */
function Activate() {
  const [stat, changeStat] = useState(0);

  const activateTextField = () => {
    changeStat(0);
  };

  const activateMap = () => {
    changeStat(1);
  };

  return (
    <div>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          "& > *": {
            m: 2,
          },
        }}
      >
        <ButtonGroup
          size="large"
          variant="text"
          aria-label="text button group"
          color="warning"
        >
          <Button onClick={activateTextField}>검색</Button>
          <Button onClick={activateMap}>지도</Button>
        </ButtonGroup>
      </Box>
      <Box
        component="form"
        sx={{
          "& .MuiTextField-root": { m: 1, width: "25ch" },
        }}
        noValidate
        autoComplete="off"
      >
        <TextField
          id="standard-search"
          label="검색"
          type="search"
          variant="standard"
          color="warning"
          focused
        />
      </Box>
      <Box
        component="form"
        sx={{
          "& .MuiTextField-root": { m: 1, width: "25ch" },
        }}
        noValidate
        autoComplete="off"
      >
        {/* <ThemeProvider theme={theme}>
        <TextField
          id="standard-search"
          label="검색"
          type="search"
          variant="standard"
          color="secondary"
          focused
        />
        </ThemeProvider>*/}
      </Box>
    </div>
  );
}

export default Activate;
