      fillColor,
      fillOpacity,
      strokeColor,
      strokeOpacity,
      strokeStyle,
      strokeWeight,
      zIndex,
      onMouseover,
      onMouseout,
      onMousemove,
      onMousedown,
      onClick,
      onCreate,
    },
    ref
