        circle.setMap(null)
      }
    }, [map, circle])

    useLayoutEffect(() => {
      if (onCreate) onCreate(circle)
    }, [circle, onCreate])

    useLayoutEffect(() => {
      if (circle) circle.setPosition(circleCenter)
    }, [circle, circleCenter])

    useLayoutEffect(() => {
