import { combineReducers } from "redux";
import { all } from "redux-saga/effects";
import post, { postSaga } from "./post";

const rootReducer = combineReducers({
  post,
});

export function* rootSaga() {
  yield all([postSaga()]);
}

export default rootReducer;
