import styled from "styled-components";
import palette from "../../lib/styles/palette";

const DetailViewerBlock = styled.div``;

const MainImage = styled.img``;

const MainTitle = styled.div``;

const Booking = styled.div``;

const Info = styled.div``;

const Tags = styled.div`
  margin-top: 0.5rem;
  .tag {
    display: inline-block;
    color: white;
    text-decoration: none;
    margin-right: 0.5rem;
    &:hover {
      color: pink;
    }
  }
`;

const DetailViewer = ({ show }) => {
  const {
    show_name,
    show_artist,
    show_search,
    show_term,
    show_num,
    show_city,
    show_place,
    show_place_eng,
    show_place_detail,
    show_price,
    show_link,
    show_hashtag,
    show_imgs,
  } = show;
  return (
    <DetailViewerBlock>
      <MainImage></MainImage>
      <MainTitle></MainTitle>
      <Booking />
      <Info>{show_name}</Info>
      <Tags>
        <div className="tag">#tag1</div>
        <div className="tag">#tag2</div>
        <div className="tag">#tag3</div>
      </Tags>
    </DetailViewerBlock>
  );
};

export default DetailViewer;
